    private void toRadButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_toRadButtonActionPerformed
        //Variables
        double toRadA;
        //convert string to integer
        toRadA = Double.parseDouble(toRadText1.getText());
        //Output answer
        answer.setText(String.valueOf(Math.toRadians(toRadA)));
    }//GEN-LAST:event_toRadButtonActionPerformed

