    private void maxButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_maxButtonActionPerformed
        //Variables
        double max1;
        double max2;
        //Convert string to integer
        max1 = Double.parseDouble(maxText1.getText());
        max2 = Double.parseDouble(maxText2.getText());
        //Output answer
        answer.setText(String.valueOf(Math.max(max1,max2)));
    }//GEN-LAST:event_maxButtonActionPerformed

